document.addEventListener("DOMContentLoaded",()=>{if(typeof window<"u"){const s=document.querySelectorAll("a[data-nav-link]"),t=window.location.pathname;s.forEach(n=>{const e=n.getAttribute("href"),o=c=>c.replace(/\/+$/,""),i=o(e)===o(t)||t.startsWith(e)&&e!=="/";console.log("Client-side href:",e),console.log("Client-side currentPath:",t),console.log("Client-side isActive:",i),i?n.classList.add("active"):n.classList.remove("active")})}});
